use bdk::bitcoin::psbt;

pub fn validate_psbt(
    signed_psbt: &psbt::PartiallySignedTransaction,
    unsigned_psbt: &psbt::PartiallySignedTransaction,
) -> bool {
    // this is a good enough check. if all the inputs have final_script_witness or final_script_sig
    // it can be safe to assume that the psbt is valid.
    // additional check now makes more sense since we are checking if both the unsigned and signed
    // psbt have same unsigned_tx
    // this also eliminates the need for xpub

    unsigned_psbt.unsigned_tx == signed_psbt.unsigned_tx
        && signed_psbt
            .inputs
            .iter()
            .all(|inp| inp.final_script_witness.is_some() || inp.final_script_sig.is_some())
}

#[cfg(test)]
mod test {
    use super::*;

    #[test]
    fn passes_if_correct_and_signed_psbt_passed() {
        let signed_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        let unsigned_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        assert!(validate_psbt(&signed_psbt, &unsigned_psbt))
    }

    #[test]
    fn fails_if_incorrect_and_signed_psbt_passed() {
        let signed_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        let unsigned_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        assert!(!validate_psbt(&signed_psbt, &unsigned_psbt))
    }

    #[test]
    fn fails_if_unsigned_psbt_passed() {
        let signed_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        let unsigned_psbt = "".parse::<psbt::PartiallySignedTransaction>().unwrap();
        assert!(!validate_psbt(&signed_psbt, &unsigned_psbt))
    }
}
